import express from 'express';
import bodyParser from 'body-parser';
import { MongoClient } from 'mongodb';
import path from 'path';

const app = express();

app.use(express.static(path.join(__dirname, '/build')));
app.use(bodyParser.json());

const withDB = async (operations, res) => {
    try {
    
        const client = await MongoClient.connect('mongodb://localhost:27017', { useUnifiedTopology: true});
        const db = client.db('mjsystem');
    
        await operations(db);

        client.close();
    } catch (error) {
        res.status(500).json({ message: 'Error Connect to db', error});
    }

}

app.get('/api/articles/:name', async (req, res) => {
    withDB( async (db) => {
        
        const articleName = req.params.name;
    
        const articleInfo = await db.collection('article').findOne({ name: articleName });
        res.status(200).json(articleInfo);

    }, res);
    
})

app.post('/api/articles/:name/upvote', async (req, res) => {

    withDB (async (db) => {
        const articleName =req.params.name;
        
        const articleInfo = await db.collection('article').findOne({ name: articleName});
        await db.collection('article').updateOne({name: articleName}, {
            '$set' : {
                upvote: articleInfo.upvote + 1,
            },
        });

        const updateArticleInfo = await db.collection('article').findOne({ name: articleName});

        res.status(200).json(updateArticleInfo);
    }, res);

});

app.post('/api/articles/:name/add-comment', async (req, res) =>{
    const { username, text} = req.body;
    const articleName = req.params.name;   

   withDB (async (db) => {

       const articleInfo = await db.collection('article').findOne({ name: articleName});
        await db.collection('article').updateOne({ name: articleName }, {
            '$set': {
                comments: articleInfo.comments.concat({ username, text })
            },
        });
        const updatedArticleInfo = await db.collection('article').findOne({name: articleName});
        res.status(200).json(updatedArticleInfo);
   }, res); 
});

app.get('x', (req, res) => {
    res.sendFile(path.join(__dirname + '/build/index.html'));
});

app.listen(8000, () => console.log('listening on port 8000'));
